CREATE FUNCTION get_amounts(OUT client_id integer, OUT m_sum numeric, OUT d_sum numeric, OUT a_sum numeric) RETURNS SETOF record
    LANGUAGE plpgsql
    AS $$
DECLARE x_date timestamp;
DECLARE m_date timestamp;
DECLARE d_date timestamp;
BEGIN
	m_date = date_trunc('month', now()::timestamp);
	d_date = date_trunc('day', now()::timestamp);    

    WITH RECURSIVE amount(client_id, m_sum, d_sum, a_sum) AS (    
        select c.id, coalesce(m.m_sum,0) m_sum, coalesce(d.d_sum,0) d_sum, coalesce(a.a_sum,0) a_sum from billing.clients c
        left join 
            (select u.client_id, sum(price_mcn) m_sum from billing.calls c
              left join billing.usage u on u.id=c.usage_id
              where 
                  c.time >= date_trunc('month',m_date) and
                  c.time <  date_trunc('month',m_date) + '1 month'::interval
              group by u.client_id) as m
        on c.id = m.client_id
        left join 
            (select u.client_id, sum(price_mcn) d_sum from billing.calls c
              left join billing.usage u on u.id=c.usage_id
              where 
                  c.time >= date_trunc('day',d_date) and
                  c.time <  date_trunc('day',d_date) + '1 day'::interval
              group by u.client_id) as d
        on c.id = d.client_id
        left join
            (select cl.id client_id, sum(price_mcn) a_sum 
                from billing.clients cl
                left join billing.usage u on u.client_id=cl.id
                left join billing.calls c
                on c.usage_id=u.id and
                      c.time >= date_trunc('month',m_date) and
                      c.time <  date_trunc('month',m_date) + '1 month'::interval
                group by cl.id) as a
        on c.id = a.client_id        
        where (m.m_sum is not null or d.d_sum is not null or a.a_sum is not null)
    )
    UPDATE billing.clients as c
    SET amount_month_sum = s.m_sum,
        amount_month = m_date,
    	amount_day_sum = s.d_sum,
        amount_day = d_date,
    	amount_sum = s.a_sum
    FROM amount as s
    WHERE c.id = s.client_id;
    
	return query 
	    select 	id, 
        		case amount_month when m_date then amount_month_sum else 0 end,
        		case amount_day when d_date then amount_day_sum else 0 end,
        		amount_sum
		from billing.clients;
END;
$$;


