CREATE FUNCTION tr_network_prefix_after() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  next_date_from DATE;
BEGIN
	IF (TG_OP = 'UPDATE') THEN
   		IF (NEW.prefix <> OLD.prefix OR NEW.instance_id <> OLD.instance_id OR NEW.operator_id <> OLD.operator_id) THEN
	   		RAISE 'Can not edit fields (prefix, instance_id, operator_id)'; RETURN NULL;    
        END IF;
   		IF (NEW.date_from = OLD.date_from) THEN
	   		RETURN NEW;    
        END IF;
        
		-- обновляем срок действия записей действие которых прекратилось на удаляемой
    	UPDATE billing.network_prefix SET date_to=OLD.date_to
        WHERE prefix=OLD.prefix and instance_id=OLD.instance_id and operator_id=OLD.operator_id and date_from<OLD.date_from and date_to = OLD.date_from-1;



		-- Находим запись с датой актуальности позже чем у вставляемой записи и получаем дату
		-- устанавливаем ставим срок действия вставляемой записи
        SELECT date_from - 1 INTO NEW.date_to FROM billing.network_prefix 
        WHERE prefix=NEW.prefix and instance_id=NEW.instance_id and operator_id=NEW.operator_id and date_from>NEW.date_from
        	and date_from<>OLD.date_from
        ORDER BY date_from ASC;

		-- обновляем срок действия действующих записей с датой актуальности раньше вставляемой
    	UPDATE billing.network_prefix SET date_to=NEW.date_from - 1
        WHERE prefix=NEW.prefix and instance_id=NEW.instance_id and operator_id=NEW.operator_id and date_from<NEW.date_from and date_to>=NEW.date_from
        	and date_from<>OLD.date_from;
        
   		RETURN NEW;    
	ELSEIF (TG_OP = 'DELETE') THEN

		-- обновляем срок действия записей действие которых прекратилось на удаляемой
    	UPDATE billing.network_prefix SET date_to=OLD.date_to
        WHERE prefix=OLD.prefix and instance_id=OLD.instance_id and operator_id=OLD.operator_id and date_from<OLD.date_from and date_to = OLD.date_from-1;

		RETURN OLD;    
	ELSEIF (TG_OP = 'INSERT') THEN
    
		-- Находим запись с датой актуальности позже чем у вставляемой записи и получаем дату
		-- устанавливаем ставим срок действия вставляемой записи
        SELECT date_from - 1 INTO NEW.date_to FROM billing.network_prefix 
        WHERE prefix=NEW.prefix and instance_id=NEW.instance_id and operator_id=NEW.operator_id and date_from>NEW.date_from
        ORDER BY date_from ASC;

		-- обновляем срок действия действующих записей с датой актуальности раньше вставляемой
    	UPDATE billing.network_prefix SET date_to=NEW.date_from - 1
        WHERE prefix=NEW.prefix and instance_id=NEW.instance_id and operator_id=NEW.operator_id and date_from<NEW.date_from and date_to>=NEW.date_from;
        
        IF NEW.date_to is null THEN
            NEW.date_to = '4000-01-01'::date;
        END IF;          
		RETURN NEW;    
    END IF;    

END;
$$;


