CREATE FUNCTION update_clients_auto_disabled() RETURNS boolean
    LANGUAGE plpgsql
    AS $$
BEGIN
    WITH RECURSIVE auto_disabled(client_id, new_voip_auto_disabled) AS (
      select	id as client_id,
              cast(		(voip_limit_day != 0 and amount_day_sum > voip_limit_day) or
                   		(voip_limit_month != 0 and amount_month_sum > voip_limit_month) or
                      	(credit != 0 and balance - amount_sum < - credit) as boolean
              ) as new_voip_auto_disabled
      from billing.clients cl
      where voip_auto_disabled <> cast(	(voip_limit_day != 0 and amount_day_sum > voip_limit_day) or
                                  		(voip_limit_month != 0 and amount_month_sum > voip_limit_month) or
                                  		(credit != 0 and balance - amount_sum < - credit) as boolean)
    )
    UPDATE billing.clients as c
    SET voip_auto_disabled = s.new_voip_auto_disabled,
        sync = true
    FROM auto_disabled as s
    WHERE c.id = s.client_id;
    
    RETURN TRUE;
END;
$$;


