CREATE FUNCTION calc() RETURNS boolean
    LANGUAGE plpgsql
    AS $$
BEGIN

	truncate geo.geo_prefix;

    insert into geo.geo_prefix(prefix, geo_id)
		select unnest(prefix), id from geo.geo;

	truncate geo.prefix;
    
    insert into geo.prefix(prefix, geo_id, mob, dest, region, operator_id)
    	select p.prefix, p.geo_id, p.prefix ~ '^79', g.dest, g.region, p.operator_id  from geo.rossvyaz_prefix p
        left join geo.geo g on p.geo_id=g.id;

	insert into geo.prefix(prefix, geo_id, mob, dest, region)
    	select p.prefix, p.geo_id, p.mob, g.dest, g.region
        from (
            select	coalesce(g.prefix, m.prefix) as prefix, 
                    coalesce(g.geo_id, geo.pre_get_geo_by_prefix(m.prefix)) as geo_id, 
                    coalesce(m.mob, geo.pre_get_mob_by_prefix(g.prefix)) as mob
            from  geo.geo_prefix as g
            full join geo.mob_prefix m on m.prefix=g.prefix
            left join geo.prefix p on g.prefix=p.prefix or m.prefix=p.prefix
            where p.prefix is null
    	) as p
        left join geo.geo g on p.geo_id=g.id;
        
    PERFORM geo.fill_prefix();
    
    PERFORM billing.mark_sync('geo_prefix',0);
        	
	return true;
END;
$$;


