CREATE FUNCTION rossvyaz_calc() RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
  cur CURSOR IS 
  	select	'7' || def::varchar prefix, 
		lpad( r.from::varchar, 7, '0') pfrom, 
        lpad( r.to::varchar, 7, '0') pto,
        g.geo_id,
        o.operator_id,
        r.oper AS rossvyaz_op_name,
        vro.id AS rossvyaz_op_id
	from geo.rossvyaz_raw r
    left join geo.rossvyaz_raw_geo g on r.geo_key = g.key
    left join geo.rossvyaz_raw_operator o on r.oper_key = o.key
    left join voip.rossvyaz_operator vro ON vro.name = r.oper
    ;
    
DECLARE p_prefix VARCHAR;
DECLARE n1 SMALLINT;
DECLARE n2 SMALLINT;
DECLARE n INTEGER;
DECLARE reg VARCHAR[];
DECLARE p_region VARCHAR;
DECLARE p_city VARCHAR;
DECLARE p_raion VARCHAR;
DECLARE rossvyaz_operator_id INTEGER;
BEGIN

  truncate geo.rossvyaz_prefix;

  FOR r IN cur LOOP

    INSERT INTO voip.rossvyaz_operator(name) VALUES (r.rossvyaz_op_name); -- try to catch new operator name
    SELECT x.id INTO rossvyaz_operator_id FROM voip.rossvyaz_operator AS x WHERE x.name = r.rossvyaz_op_name; -- and use ID found by operator name
    IF rossvyaz_operator_id IS NULL THEN
	RAISE EXCEPTION 'inserted operator % not selected', r.name;
    END IF;
    IF r.rossvyaz_op_id IS NULL THEN
      r.rossvyaz_op_id = rossvyaz_operator_id; -- use new data
    END IF;
  
    p_prefix = r.prefix;
    n1 = 1;
    n2 = 7;
    
    while n1 <= n2 loop
    	if substring(r.pfrom from n1 for 1) = substring(r.pto from n1 for 1) then
            n1 = n1 + 1;
        else
        	exit;
        end if;
    end loop;  
    
    while n2 >= n1 loop
    	if substring(r.pfrom from n2 for 1) = '0' and substring(r.pto from n2 for 1) = '9' then
            n2 = n2 - 1;
        else
        	exit;
        end if;    
    end loop;
    
    if n1 > 1 then
		p_prefix = p_prefix || substring(r.pfrom from 1 for n1-1);
    end if;    
    
    if n1 <= n2 then
        insert into geo.rossvyaz_prefix(prefix, geo_id, operator_id, rossvyaz_operator_id)
            select  p_prefix || geo.explode_def(substring(r.pfrom from n1 for n2-n1+1), substring(r.pto from n1 for n2-n1+1)),
  					r.geo_id, r.operator_id, r.rossvyaz_op_id;
    else
    	insert into geo.rossvyaz_prefix(prefix, geo_id, operator_id, rossvyaz_operator_id)
        					VALUES(p_prefix, r.geo_id, r.operator_id, r.rossvyaz_op_id);
    end if;
    
  END LOOP;
  
  return null;
END;
$$;


