CREATE FUNCTION tr_geo_before_insert_update() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    if (TG_OP = 'INSERT') then  
    
		if new.country is not null then
	    	select "name" into new.country_name from geo.country where id=new.country;
        end if;

		if new.fo is not null then
	    	select "name" into new.fo_name from geo.fo where id=new.fo;
        end if;

		if new.region is not null then
	    	select "name" into new.region_name from geo.region where id=new.region;
        end if;
        
		if new.district is not null then
	    	select "name" into new.district_name from geo.district where id=new.district;
        end if;

		if new.city is not null then
	    	select "name" into new.city_name from geo.city where id=new.city;
        end if;
        
        
	elseif (TG_OP = 'UPDATE') then
    
    	if	(new.country is null and old.country is not NULL) or 
	        (new.country is not null and old.country is NULL) or 
        	(new.country <> old.country) then
	    	select "name" into new.country_name from geo.country where id=new.country;
        end if;

    	if	(new.fo is null and old.fo is not NULL) or 
	        (new.fo is not null and old.fo is NULL) or 
        	(new.fo <> old.fo) then
	    	select "name" into new.fo_name from geo.fo where id=new.fo;
        end if;

    	if	(new.region is null and old.region is not NULL) or 
	        (new.region is not null and old.region is NULL) or 
        	(new.region <> old.region) then
	    	select "name" into new.region_name from geo.region where id=new.region;
        end if;

    	if	(new.district is null and old.district is not NULL) or 
	        (new.district is not null and old.district is NULL) or 
        	(new.district <> old.district) then
	    	select "name" into new.district_name from geo.district where id=new.district;
        end if;

    	if	(new.city is null and old.city is not NULL) or 
	        (new.city is not null and old.city is NULL) or 
        	(new.city <> old.city) then
	    	select "name" into new.city_name from geo.city where id=new.city;
        end if;                

    end if;

	IF new.country = 7 or new.id IN (7336) then
		new.dest = 1;
    elseif new.country IN (994, 374, 7840, 375, 77, 996, 373, 995, 992, 993, 998, 380) then
	    new.dest = 3;
    else
	    new.dest = 2;
    end if;

	-- Формируем наименование 
	NEW.name = new.country_name;
         
	IF (NEW.country = 7) THEN
    	IF (new.region_name <> '') THEN
	        NEW.name = new.region_name;
        END IF;
    ELSEIF (new.region_name <> '') THEN 
    	NEW.name = NEW.name || ' ' || new.region_name;
    END IF;
    
    IF (new.city_name <> '' and new.city_name <> new.region_name) THEN 
    	NEW.name = NEW.name || '. ' || new.city_name;
    END IF;
    	 
    IF (new.info_name <> '') THEN
	    NEW.name = NEW.name || ' (' || new.info_name || ')';
    END IF;
    
  
	return new;
END;
$$;


