CREATE FUNCTION explode_def(s1 character varying, s2 character varying, OUT code character varying) RETURNS SETOF character varying
    LANGUAGE plpgsql IMMUTABLE
    AS $$
DECLARE
	n INTEGER;
    n1 INTEGER;
    n2 INTEGER;
    l INTEGER;
    ss1 VARCHAR;
    ss2 VARCHAR;
    
BEGIN
	l = char_length(s2);
    
    while l > 0 loop
    	n1 = cast( substring(s1 from l for 1) as integer);
    	n2 = cast( substring(s2 from l for 1) as integer);

        ss1 = substring(s1 from l + 1);
        if ss1 <> lpad('',char_length(ss1), '0') then
            n1 = n1 + 1;
        end if;
        ss2 = substring(s2 from l + 1);            
        if ss2 <> lpad('',char_length(ss2), '9') then
            n2 = n2 - 1;
        end if;            

        if substring(s1 from 1 for l-1) = substring(s2 from 1 for l-1) then
            n = n1;            
            while n <= n2 loop        	
                code = substring(s1 from 1 for l-1) || cast(n as varchar);
                return next;
                n = n + 1;
            end loop;
        else
            n = n1;
            while n > 0 and n <= 9 loop
                code = substring(s1 from 1 for l-1) || cast(n as varchar);
                return next;
                n = n + 1;
            end loop;
            
            n = 0;
            while n2 < 9 and n <= n2 loop
                code = substring(s2 from 1 for l-1) || cast(n as varchar);
                return next;
                n = n + 1;
            end loop;
        end if;
        
    	l = l - 1;
    end loop;

	return;
END;
$$;


