CREATE FUNCTION new_destinations(p_rawfile_id integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE 
  c_desttoinsert CURSOR IS 
  	SELECT r.ndef
  	FROM voip.raw_price r LEFT JOIN voip_destinations d ON r.ndef=d.ndef
    WHERE d.ndef IS NULL and (p_rawfile_id is null or r.rawfile_id=p_rawfile_id) 
    GROUP BY r.ndef;
  c_desttoupdate CURSOR IS 
  	SELECT d.ndef
  	FROM voip_destinations d
    WHERE d.geo_id IS NULL;
 
	affected INTEGER;   
BEGIN
	affected = 0;

	FOR r IN c_desttoinsert LOOP
		insert into voip_destinations(ndef) values(r.ndef);
		affected = affected + 1;
	END LOOP; 

	if p_rawfile_id is null then
      FOR r IN c_desttoupdate LOOP
          update voip_destinations set geo_id = 0 where ndef=r.ndef;      	
          affected = affected + 1;
      END LOOP; 
    end if;
    
    RETURN affected;
END;
$$;


