CREATE FUNCTION select_rawfile_diff(p_rawfile_id integer, OUT ndef bigint, OUT inserting boolean, OUT deleting boolean, OUT new_price numeric, OUT old_price numeric) RETURNS SETOF record
    LANGUAGE plpgsql
    AS $$
DECLARE
	p_pricelist_id INTEGER;
	p_file_active BOOLEAN;
	p_file_full BOOLEAN;
BEGIN

	SELECT r.active, r."full", r.pricelist_id INTO p_file_active, p_file_full, p_pricelist_id FROM voip.raw_file r WHERE r.id = p_rawfile_id;
    
    IF p_file_active THEN
    
	    RETURN QUERY
    	SELECT r.ndef, r.change_price is NULL AND NOT r.deleting, r.deleting, r.price, r.change_price
        FROM voip.raw_price r
        WHERE r.rawfile_id=p_rawfile_id AND r.change_type IS NOT NULL
        	AND (r.change_price is NULL OR r.deleting OR r.price<>r.change_price);
    
    ELSEIF p_file_full THEN

	    RETURN QUERY
		SELECT 
			CASE WHEN r.ndef IS NULL THEN d.ndef ELSE r.ndef END, -- ndef
            d.price IS NULL AND r.ndef IS NOT NULL, -- inserting
			CASE WHEN r.ndef IS NULL THEN true ELSE r.deleting END, -- deleting
			CASE WHEN r.ndef IS NULL OR r.deleting THEN d.price ELSE r.price END, -- price
			d.price -- change_price
		FROM (select * from voip.raw_price where rawfile_id=p_rawfile_id) r
		FULL JOIN select_defs_price(p_pricelist_id, null) d ON r.ndef=d.ndef
        WHERE (d.price IS NULL OR CASE WHEN r.ndef IS NULL THEN true ELSE r.deleting END OR CASE WHEN r.ndef IS NULL OR r.deleting THEN d.price ELSE r.price END<>d.price);
                              
    ELSE
    
	    RETURN QUERY
		SELECT r.ndef, r.change_price is NULL AND NOT r.deleting, r.deleting,
        	CASE WHEN r.deleting THEN d.price ELSE r.price END as price,
            d.price as change_price
        FROM voip.raw_price r
        LEFT JOIN select_defs_price(p_pricelist_id, null) d ON r.ndef=d.ndef
        WHERE r.rawfile_id=p_rawfile_id
        	AND (r.change_price is NULL OR r.deleting OR CASE WHEN r.deleting THEN d.price ELSE r.price END<>d.price);
        
    END IF;    
    
END;
$$;


