CREATE FUNCTION activate_network_file(p_network_file_id integer) RETURNS boolean
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  cur_raw_prices CURSOR (p_network_file_id integer, p_instance_id SMALLINT, p_operator_id SMALLINT, p_startdate DATE) IS 
  	SELECT r.prefix, r.deleting, r.network_type_id,
	    d.date_from last_date_from, d.deleted last_deleted, d.network_type_id last_network_type_id
    FROM voip.network_file_data r
        LEFT JOIN billing.network_prefix d 
        ON	d.prefix=r.prefix and 
  			d.instance_id=p_instance_id and
            d.operator_id=p_operator_id and
			d.date_from <= p_startdate and
			d.date_to >= p_startdate
    WHERE r.network_file_id=p_network_file_id;
     
  file_instance_id SMALLINT;
  file_operator_id SMALLINT;
  file_start_date DATE;
  file_active BOOLEAN;
BEGIN

  SELECT f.active, c.instance_id, c.operator_id, f.startdate INTO file_active, file_instance_id, file_operator_id, file_start_date 
  FROM voip.network_file f
  LEFT JOIN voip.network_config c on c.id=f.network_config_id
  WHERE f.id=p_network_file_id;
  
  IF file_active = TRUE THEN
    RAISE 'File already activated'; RETURN FALSE;
  END IF;
  
  INSERT INTO voip.network_file_data(network_file_id, prefix, deleting, network_type_id)
          SELECT p_network_file_id, r.prefix, TRUE, r.network_type_id  FROM billing.network_prefix r
                  LEFT JOIN voip.network_file_data p ON network_file_id=p_network_file_id AND r.prefix=p.prefix
          WHERE r.instance_id=file_instance_id and r.operator_id=file_operator_id and r.deleted=false and 
                  r.date_from <= CAST(now() as DATE) and r.date_to >= CAST(now() as DATE)                    
                  and p.prefix IS NULL;

  FOR r IN cur_raw_prices(p_network_file_id, file_instance_id, file_operator_id, file_start_date) LOOP
  	IF voip.activate_network_file_line(p_network_file_id, r.prefix, file_instance_id, file_operator_id, file_start_date, r.deleting, r.network_type_id,
    									r.last_date_from, r.last_deleted, r.last_network_type_id) = FALSE THEN
    	RETURN FALSE;
    END IF;
    
  END LOOP;
  
  PERFORM billing.mark_sync('network_prefix',0);  

  RETURN TRUE;
END;
$$;


