CREATE FUNCTION activate_raw_file(p_rawfile_id integer) RETURNS boolean
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  cur_raw_prices CURSOR (p_rawfile_id integer, p_pricelist_id SMALLINT,  p_startdate DATE) IS 
  	SELECT r.ndef, r.deleting,
	    case i.region_id @> ARRAY[g.region]::varchar[] 
                    when true then 
                        case r.price > 0
                        when true then r.price - pp.initiate_zona_cost
                        else 0 end
                    else 
                        case r.price > 0
                        when true then r.price - pp.initiate_mgmn_cost
                        else 0 end
                    end as price,
	    d.date_from last_date_from, d.deleted last_deleted, d.price last_price
    FROM voip.raw_price r
        left join voip.raw_file f on f.id = r.rawfile_id
        left join voip.pricelist pp on pp.id = f.pricelist_id
        left join billing.instance_settings i on i.id = pp.region
        left join voip_destinations dd on r.ndef = dd.ndef
        left join geo.geo g on g.id = dd.geo_id
        LEFT JOIN billing.defs d ON d.ndef=r.ndef and d.pricelist_id=p_pricelist_id and
					        	d.date_from <= p_startdate and
					        	d.date_to >= p_startdate
    WHERE r.rawfile_id=p_rawfile_id;
     
  file_pricelist_id SMALLINT;
  file_startdate DATE;
  file_active BOOLEAN;
  file_full BOOLEAN;
BEGIN

  SELECT f.active, f."full", f.pricelist_id, f.startdate INTO file_active, file_full, file_pricelist_id, file_startdate 
  FROM voip.raw_file f 
  LEFT JOIN voip.pricelist p ON p.id=f.pricelist_id
  WHERE f.id=p_rawfile_id;
  
  IF file_active = TRUE THEN
    RAISE 'File already activated'; RETURN FALSE;
  END IF;
  
  IF file_full = TRUE THEN

  
		INSERT INTO voip.raw_price (rawfile_id, ndef, deleting, price)
				SELECT p_rawfile_id, r.ndef, TRUE,
					case i.region_id @> ARRAY[g.region]::varchar[] 
                    when true then 
                        case r.price > 0
                        when true then r.price + pp.initiate_zona_cost
                        else 0 end
                    else 
                        case r.price > 0
                        when true then r.price + pp.initiate_mgmn_cost
                        else 0 end
                    end as price
                FROM billing.defs r
						LEFT JOIN voip.raw_price p ON rawfile_id=p_rawfile_id AND r.ndef=p.ndef
                        left join voip.pricelist pp on pp.id = r.pricelist_id
                        left join billing.instance_settings i on i.id = pp.region
                        left join voip_destinations dd on r.ndef = dd.ndef
				        left join geo.geo g on g.id = dd.geo_id
				WHERE r.pricelist_id=file_pricelist_id and r.deleted=false and 
						r.date_from <= CAST(now() as DATE) and r.date_to >= CAST(now() as DATE)                    
					    and p.ndef IS NULL;

      
  END IF;

  FOR r IN cur_raw_prices(p_rawfile_id, file_pricelist_id, file_startdate) LOOP
  	IF voip.activate_raw_file_price(p_rawfile_id, r.ndef, file_pricelist_id, file_startdate, r.deleting, r.price,
    									r.last_date_from, r.last_deleted, r.last_price) = FALSE THEN
    	RETURN FALSE;
    END IF;
    
  END LOOP;
  
  PERFORM billing.mark_sync('defs',0);  

  RETURN TRUE;
END;
$$;


