    LANGUAGE plpgsql
    AS $$
DECLARE
 p_instance_id SMALLINT;
 p_operator_id SMALLINT;
 p_startdate DATE;
 p_change_type CHAR;
 p_old_network_type_id NUMERIC;
 p_old_deleted BOOLEAN;
