CREATE FUNCTION op_compare(p_operators smallint[], p_region integer) RETURNS TABLE(p_def character varying, p_opers smallint[])
    LANGUAGE plpgsql
    AS $$
DECLARE p_report_id INTEGER = 10;
DECLARE
	cur1 CURSOR(p_report_id INTEGER, p_operators SMALLINT[])  IS 
	  	select r.p_def, r.p_opers, r.p_zone, r.p_zone || r.p_opers::varchar as hash  from voip.op_compare_pre(p_report_id,p_operators,p_region) as r
		order by r.p_def;    

    m_def VARCHAR[];
    m_hash VARCHAR[];    
    
    pre_def VARCHAR;    
	pre_hash VARCHAR;    

    def VARCHAR;    
	hash VARCHAR;    
    
	pre_l INTEGER;
	cur_l INTEGER;
    
    n INTEGER;
BEGIN

	pre_l = 0;
    
    pre_def = null;
    pre_hash = null;    
    FOR r IN cur1(p_report_id, p_operators) LOOP
        
		cur_l = char_length(r.p_def);
				
        if pre_l <> cur_l or substr(r.p_def, 1, cur_l-1) <> substr(r.p_def, 1, pre_l-1) then
			if pre_l > cur_l then
            	n = pre_l; 
            else
            	n = cur_l;
            end if;
            while n > 0 loop
                if m_def[n] is null or m_def[n] <> substr(r.p_def, 1, char_length(m_def[n])) then
                    m_def[n] = null;
                    m_hash[n] = null;
                end if;
                n = n - 1;
            end loop;
	        pre_def = null;            
            pre_hash = null;            
            n = cur_l - 1;
            while n > 0 loop
            	if pre_def is null and m_def[n] is not null then
                	pre_def = m_def[n];
                end if;
            	if pre_hash is null and m_hash[n] is not null then
                	pre_hash = m_hash[n];
                end if;
                n = n - 1;
            end loop;
        end if;
        m_def[cur_l] = r.p_def;
		m_hash[cur_l] = r.hash;

        if position(pre_def in r.p_def) = 1 and r.hash = pre_hash then
        	continue;
        end if;
		
        p_def = r.p_zone || r.p_def;
        p_opers = r.p_opers;        
        return next;
        
    END LOOP;

	return;
END;
$$;


