CREATE FUNCTION op_compare_pre(p_report_id integer, p_operators smallint[], p_region integer) RETURNS TABLE(p_def character varying, p_zone character varying, p_opers smallint[])
    LANGUAGE plpgsql
    AS $$
DECLARE p_pricelists INTEGER[];
DECLARE cur_sel CURSOR (p_report_id integer) IS 
 				select 
                    r.ndef::varchar as defcode, 
					CASE g.country WHEN 7 THEN
					CASE g.region WHEN 7495 THEN
					CASE d.mob WHEN true THEN 'Z' ELSE 'L' END 
					ELSE 'N' END
					ELSE 'I' END as zone,
                    pl.operator_id,
                    r.price
				from voip.pricelist_report_data r
					left join voip.pricelist_report_params p on p.report_id=r.report_id and p."position"=r."position" and p.param='d1'
                    left join voip.pricelist pl on pl.id = p.pricelist_id
					left join voip_destinations d on d.ndef=r.ndef
					left join geo.geo g on g.id=d.geo_id
				where r.report_id=10 and r.price is not null
				order by defcode, r.price, r.position;
DECLARE p_operator_id SMALLINT;
DECLARE p_price NUMERIC;

BEGIN
	DELETE FROM voip.pricelist_report WHERE id = p_report_id;
    
    INSERT INTO voip.pricelist_report(id, generated)values(p_report_id, null);

	select ARRAY(  select default_pricelist_id from voip.operator where id = ANY( p_operators ) and default_pricelist_id is not null and region=p_region )
    		into p_pricelists;

	FOR i IN array_lower(p_pricelists,1) .. array_upper(p_pricelists,1) LOOP
    	INSERT INTO voip.pricelist_report_params(report_id, position, param, pricelist_id, "date")
        	values(p_report_id, i, 'd1', p_pricelists[i], now());
	END LOOP;
    
    PERFORM voip.prepare_pricelist_report(p_report_id);

	p_def = '';
    p_zone = '';
    p_opers = null;

    FOR r IN cur_sel(p_report_id) LOOP
        
        if p_def <> r.defcode then
        	if p_def <> '' then
            	RETURN next;
            end if;
            p_def = r.defcode;
            p_zone = r.zone;
            p_opers = null;
		end if;
       	p_opers = array_append(p_opers, r.operator_id); 
    END LOOP;
    if p_def <> '' then
        RETURN next;
    end if;
	return;
END;
$$;


