    LANGUAGE plpgsql
    AS $$
BEGIN
	RETURN QUERY
    	select 
            cast(
            	case count(*) when 1 then 
                	substring(p.prefix from 1 for char_length(p.prefix)-1) 
                    || max(substring(p.prefix from char_length(p.prefix))::varchar) 
                else 
	                substring(p.prefix from 1 for char_length(p.prefix)-1) 
                end 
                as varchar) as def_prefix,
            cast(
