        from 
            (select cast(unnest(region_id) as integer) as region_id from billing.instance_settings where id = p_instance_id) as r
            left join geo.prefix p on p.region=r.region_id and p.mob
            left join geo.operator o on p.operator_id = o.id
        where p.operator_id is not null
        group by substring(p.prefix from 1 for char_length(p.prefix)-1), p.operator_id, o.name
        order by def_prefix
    ;
END;
$$;


