delete from voip.analyze_pricelist_report_data where report_id=p_report_id;

FOR r IN cur LOOP
	insert into voip.analyze_pricelist_report_data(report_id, position, param, ndef,  date_from, price, effndef)
		SELECT	p_report_id,
        		r.position,
        		r.param,
        		d.ndef,
		        d.date_from,
		        d.price,
                d.ndef
		FROM billing.defs d
		WHERE 	d.pricelist_id = r.pricelist_id and
        		d.date_from <= r.date AND
				d.date_to >= r.date AND
				d.deleted = false;
END LOOP;

insert into voip.analyze_pricelist_report_data(report_id, position, param, ndef)
select r.report_id, r.position, r.param, r.ndef from 
		(	select r.report_id, r.position, r.param, d.ndef from voip.analyze_pricelist_report_params r,
        	(select distinct ndef from voip.analyze_pricelist_report_data where report_id=p_report_id) d
	        where report_id=p_report_id and param IN ('d1','d2')
        ) as r
left join voip.analyze_pricelist_report_data t on r.report_id=t.report_id and r.position=t.position and r.param=t.param and r.ndef=t.ndef
where t.ndef is null;



p_last_position = -1;
p_last_param = '  ';
FOR r IN cur2 LOOP
		to_update = FALSE;
		if p_last_position <> r.position or p_last_param <> r.param then
			p_last_position = r.position;
            p_last_param = r.param;
			pre_l = 0;
			c_defcode = null;
			c_price = null;	
			c_date = null;	
