				if c_defcode[n] is null or c_defcode[n] <> substr(r.defcode, 1, char_length(c_defcode[n])) then
					c_defcode[n] = null;
					c_price[n] = null;	
					c_date[n] = null;	
					c_effndef[n] = null;	
				else 
					if c_defcode[n] is not null then
					--	n = 0;
					end if;
				end if;
				n = n - 1;
			end loop;	

			pre_price = null;
			pre_date = null;
			pre_effndef = null;
			n = cur_l-1;
			while n > 0 loop
				if pre_price is null and c_price[n] is not null then
					pre_price = c_price[n];
				end if;
				if pre_date is null and c_date[n] is not null then
					pre_date = c_date[n];
				end if;
				if pre_effndef is null and c_effndef[n] is not null then
					pre_effndef = c_effndef[n];
				end if;
				n = n - 1;
			end loop;
		end if;

		c_defcode[cur_l] = r.defcode; 

		if pre_price is not null and p_price is null then
			p_price = pre_price;
			p_date = pre_date;
			p_effndef = pre_effndef;
            to_update=true;
		end if;

		c_price[cur_l] = p_price;
		c_date[cur_l] = p_date;
		c_effndef[cur_l] = p_effndef;
        
        if to_update then
        	
