CREATE FUNCTION rossvyaz_routing_by_reg(in_region_id integer) RETURNS TABLE(def_prefix character varying, suffix integer[], operator_id integer, name character varying)
    LANGUAGE plpgsql
    AS $$
DECLARE code_min CHARACTER VARYING = NULL;
DECLARE code_cur CHARACTER VARYING = NULL;
DECLARE pfx_min CHARACTER VARYING = NULL;
DECLARE pfx_cur CHARACTER VARYING = NULL;
DECLARE pfx_last CHARACTER VARYING = NULL;
DECLARE pfx_min_l INTEGER = 0;
DECLARE pfx_cur_l INTEGER = 0;
DECLARE oper_min_id INTEGER = NULL;
DECLARE oper_name CHARACTER VARYING = NULL;
DECLARE r_suffix INTEGER[] = NULL;
DECLARE tchar CHARACTER VARYING = NULL;
DECLARE	cursuf CURSOR(op_reg_id INTEGER) IS
	SELECT grp.prefix, grp.rossvyaz_operator_id, vro.name AS rossvyaz_name
	FROM geo.rossvyaz_prefix AS grp
	INNER JOIN voip.rossvyaz_operator AS vro ON vro.id = grp.rossvyaz_operator_id
	INNER JOIN geo.geo AS gg ON gg.id = grp.geo_id
	INNER JOIN geo.region AS gr ON gg.region = gr.id AND gr.operator_region = op_reg_id
	WHERE grp.prefix::TEXT like '79%' -- mobile only, reject fixed areacodes!
	ORDER BY char_length(grp.prefix::TEXT), grp.prefix;
BEGIN
    def_prefix = NULL; -- init returned values to empty
    suffix = NULL;
    operator_id = NULL;
    "name" = NULL;
    
    FOR r IN cursuf(in_region_id) LOOP
	IF pfx_min IS NULL THEN -- initially nothing is filled in:
		pfx_min = r.prefix; -- starting code will be from here
		oper_min_id = r.rossvyaz_operator_id;
		pfx_min_l = char_length(pfx_min);
		code_min = SUBSTRING(pfx_min FROM 1 FOR pfx_min_l-1); -- cut all digits before last to prefix: get base code
		tchar = SUBSTRING(pfx_min FROM pfx_min_l FOR 1); -- get last digit
		r_suffix = array_append(r_suffix, tchar::INTEGER); -- append last digit to empty array
		oper_name = r.rossvyaz_name;
		pfx_last = pfx_min; -- last seen prefix will be our first seen, initially
		CONTINUE; -- check next areacode
	END IF;
	
	pfx_cur = r.prefix; -- get next (current) prefix info
	pfx_cur_l = char_length(pfx_cur);
	code_cur = SUBSTRING(pfx_cur FROM 1 FOR pfx_cur_l-1); -- extract base prefix code
	
	IF pfx_cur_l = pfx_min_l -- same length as previous prefix
	   AND code_min = code_cur -- same prefix code base
	   AND r.rossvyaz_operator_id = oper_min_id  -- and belongs to same operator
	   AND ((pfx_min::BIGINT) - (pfx_cur::BIGINT) <= 9) -- and not next decade
	   THEN -- append
		 -- can merge to array:
		tchar = SUBSTRING(pfx_cur FROM pfx_cur_l FOR 1);  -- get last digit and...
		r_suffix = array_append(r_suffix, tchar::INTEGER); -- append last digit to results array
		pfx_last = pfx_cur; -- update, save last accounted prefix for step calculation
		CONTINUE; -- itterate to next prefix
	ELSE -- something went wrong and we can't append, must switch to next regexp (operator change or decade change)
		def_prefix = code_min;      code_min = NULL; -- return results
		suffix = r_suffix;          r_suffix = NULL;
		operator_id = oper_min_id;  oper_min_id = NULL;
		name = oper_name;           oper_name = NULL;
		
		pfx_cur = NULL; -- clean
		
		pfx_min = r.prefix; -- start new iteration:
		oper_min_id = r.rossvyaz_operator_id;
		pfx_min_l = char_length(pfx_min);
		oper_name = r.rossvyaz_name;
		code_min = SUBSTRING(pfx_min FROM 1 FOR pfx_min_l-1); -- cut all digits before last to prefix: get base code
		tchar = SUBSTRING(pfx_min FROM pfx_min_l FOR 1); -- last digit
		r_suffix = array_append(r_suffix, tchar::INTEGER); -- append last digit to empty array
		pfx_last = pfx_min; -- last seen will be our first prefix, again
		
		return next; -- switch to next output row
	END IF;
    END LOOP;
    return;
END;
$$;


