CREATE FUNCTION select_routing_report_old(p_report_id integer, OUT prefix character varying, OUT pricelists integer[], OUT prices numeric[], OUT priorities integer[], OUT locked boolean, OUT dest smallint, OUT country integer, OUT region integer, OUT flag character varying) RETURNS SETOF record
    LANGUAGE plpgsql
    AS $$
DECLARE p_gen_date timestamp;
DECLARE p_region_id INTEGER;
DECLARE m_geo_region_id INTEGER[];
DECLARE m_geo_city_prefix INTEGER[];
DECLARE p_regexp VARCHAR;
BEGIN

	select r.region, r.generated into p_region_id, p_gen_date from voip.routing_report r where r.id=p_report_id;

	select r.region_id, r.city_prefix into m_geo_region_id, m_geo_city_prefix from billing.instance_settings r where r.id=p_region_id;
    
    if p_gen_date is null then
    	delete from voip.routing_report_data where report_id=p_report_id;
        insert into voip.routing_report_data 
        			(report_id, prefix, pricelists, prices, priorities, locked)
    		select p_report_id, r.prefix, r.pricelists, r.prices, r.priorities, r.locked
	        from voip.prepare_routing_report(p_report_id) r;
        update voip.routing_report set generated = CURRENT_TIMESTAMP where id=p_report_id;
    end if;

    p_regexp = '^(' || array_to_string(m_geo_city_prefix, '|') || ')';

	RETURN QUERY 
    	select r.prefix, r.pricelists, r.prices, r.priorities, r.locked, g.dest, g.country, g.region,
        	case g.dest 
            when 3 then 'I'::varchar
            when 2 then 'I'::varchar
            when 1 then 
            	case r.prefix ~ p_regexp
                when true then 'L'::varchar
                else
	            	case g.region = ANY(m_geo_region_id) 
    	            when true then 'Z'::varchar
        	        else 'N'::varchar
                    end
                end
            else
            	'I'::varchar
            end
        from voip.routing_report_data r
        left join voip_destinations d on r.prefix=d.defcode
		left join geo.geo g on g.id=d.geo_id
        where r.report_id=p_report_id;
END;
$$;


