CREATE FUNCTION tr_rossvyaz_operator_before_ins() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE t_id INTEGER;
DECLARE t_ch VARCHAR;
BEGIN
IF ( TG_OP = 'DELETE' ) THEN
	return OLD; -- just delete
ELSIF ( 'INSERT' = TG_OP ) THEN
	IF NEW.name IS NOT NULL THEN -- given "name" column: try to find row by "name"
		SELECT id INTO t_id FROM voip.rossvyaz_operator WHERE "name" = NEW.name;
		IF t_id IS NOT NULL THEN
			return NULL;-- name is here already: no need to update
		END IF;
		-- ID was not found by "name", try to find row by ID and update "name" or insert new one
		IF NEW.id IS NOT NULL THEN-- complete insert with ID
			SELECT name INTO t_ch FROM voip.rossvyaz_operator WHERE "id" = NEW.id;
			IF t_ch IS NOT NULL THEN -- NEW.id is used already: can't insert
				return NULL;
			END IF;
			return NEW;
		ELSE -- new ID is NULL or was not found, possibly partial insert by just "name"
			SELECT (MAX(id)::INTEGER)+1 INTO t_id FROM voip.rossvyaz_operator; -- get next available ID
			IF t_id IS NULL THEN t_id = 1; END IF;
			NEW.id = t_id;
			return NEW;
		END IF;
	ELSE -- name is NULL, can't insert just ID
		RAISE 'column "name" could not be NULL';
	END IF;
END IF;
return NEW; -- just update ...
END
$$;


