CREATE FUNCTION trproc_defs_actions() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  next_date_from DATE;
BEGIN
	IF (TG_OP = 'UPDATE') THEN
   		IF (NEW.ndef <> OLD.ndef OR NEW.pricelist_id <> OLD.pricelist_id) THEN
	   		RAISE 'Can not edit fields (ndef, pricelist_id)'; RETURN NULL;    
        END IF;
   		IF (NEW.date_from = OLD.date_from) THEN
	   		RETURN NEW;    
        END IF;
        
		-- обновляем срок действия записей действие которых прекратилось на удаляемой
    	UPDATE billing.defs SET date_to=OLD.date_to
        WHERE ndef=OLD.ndef and pricelist_id=OLD.pricelist_id and date_from<OLD.date_from and date_to = OLD.date_from-1;



		-- Находим запись с датой актуальности позже чем у вставляемой записи и получаем дату
		-- устанавливаем ставим срок действия вставляемой записи
        SELECT date_from - 1 INTO NEW.date_to FROM billing.defs 
        WHERE ndef=NEW.ndef and pricelist_id=NEW.pricelist_id and date_from>NEW.date_from
        	and date_from<>OLD.date_from
        ORDER BY date_from ASC;

		-- обновляем срок действия действующих записей с датой актуальности раньше вставляемой
    	UPDATE billing.defs SET date_to=NEW.date_from - 1
        WHERE ndef=NEW.ndef and pricelist_id=NEW.pricelist_id and date_from<NEW.date_from and date_to>=NEW.date_from
        	and date_from<>OLD.date_from;
        
   		RETURN NEW;    
	ELSEIF (TG_OP = 'DELETE') THEN

		-- обновляем срок действия записей действие которых прекратилось на удаляемой
    	UPDATE billing.defs SET date_to=OLD.date_to
        WHERE ndef=OLD.ndef and pricelist_id=OLD.pricelist_id and date_from<OLD.date_from and date_to = OLD.date_from-1;

		RETURN OLD;    
	ELSEIF (TG_OP = 'INSERT') THEN
    
    	NEW.defcode = cast(NEW.ndef as VARCHAR);
        
		-- Находим запись с датой актуальности позже чем у вставляемой записи и получаем дату
		-- устанавливаем ставим срок действия вставляемой записи
        SELECT date_from - 1 INTO NEW.date_to FROM billing.defs 
        WHERE ndef=NEW.ndef and pricelist_id=NEW.pricelist_id and date_from>NEW.date_from
        ORDER BY date_from ASC;

		-- обновляем срок действия действующих записей с датой актуальности раньше вставляемой
    	UPDATE billing.defs SET date_to=NEW.date_from - 1
        WHERE ndef=NEW.ndef and pricelist_id=NEW.pricelist_id and date_from<NEW.date_from and date_to>=NEW.date_from;
        
        IF NEW.date_to is null THEN
            NEW.date_to = '4000-01-01'::date;
        END IF;          
		RETURN NEW;    
    END IF;    

END;
$$;


